import React, { useState } from 'react';

export default function Category() {
  const [activeId, setActiveId] = useState(1);

  const onTabClick = (id) => {
    setActiveId(id);
  };

  return (
    <Box>
      <TaskBar
        categories={categories}
        activeId={activeId}
        onTabClick={onTabClick}
        title="Category"
      />
      {categories.map(
        (category) =>
          category.id === activeId && (
            /* category.listItem &&  */
            <ListItem
              key={category.id}
              listItem={category.listItem}
              api={category.api}
            />
          )
      )}
    </Box>
  );
}

